Map:

&borders-lowzoom;
&water-lowzoom;
&landuse;
&landuse2;
&glaciers;
&borders;
&buildings;
&roads-casing;
&roads-fill;
&places-cutout;
&places;



&basemap;


	water-lowzoom

		shape
		data/water/water




	borders-lowzoom

		shape
		data/borders/borders




	landuse

		(SELECT way,way_area,landuse,leisure,"natural",wetland,wood,amenity FROM &prefix;_polygon) AS landuse 
		&datasource-settings;



&hillshade;


	landuse2

		(SELECT way,way_area,landuse,leisure,"natural",wetland,wood,amenity FROM &prefix;_polygon) AS landuse 
		&datasource-settings;




	glaciers

		(SELECT way,"natural" FROM &prefix;_polygon) AS glaciers 
		&datasource-settings;






	borders

		(SELECT way,boundary,admin_level,name FROM &prefix;_line WHERE boundary IN ('administrative')) AS borders 
		&datasource-settings;




	buildings

		(SELECT way,building,leisure,railway,amenity,aeroway FROM &prefix;_polygon WHERE (building IS NOT NULL AND building != 'no') OR (aeroway = 'terminal')) AS buildings 
		&datasource-settings;





	roads-casing
	roads-fill

		(SELECT way,highway,service,surface,tracktype,embankment FROM &prefix;_line WHERE highway IS NOT NULL) AS roads 
		&datasource-settings;




	places-cutout

		(SELECT way,name,place FROM &prefix;_point) AS places 
		&datasource-settings;
